package java_programs;
import java.util.*;

/**
 *
 * @author derricklin
 */
public class DepthFirstSearch {
    public static boolean depthFirstSearch(Node startnode, Node goalnode) {
        Set<Node> nodesVisited = new HashSet<>();
        class DepthFirstSearchHelper {
            boolean search(Node node) {
                if (nodesVisited.contains(node)) {
                    return false;
                } else if (node == goalnode) {
                    return true;
                } else {
                    for (Node successorNode : node.getSuccessors()) {
                        if (search(successorNode)) { return true; }
                    }
                }
                return false;
            }
        };

        DepthFirstSearchHelper dfs = new DepthFirstSearchHelper();
        return dfs.search(startnode);
    }

}
